/* extr.xml */
int EXTR(context *ctx, Instruction *dec)
{
	int rc = DECODE_STATUS_UNMATCHED;
	/* class general */
	/* sf=x|op21=00|100111|N=x|o0=0|Rm=xxxxx|imms=xxxxxx|Rn=xxxxx|Rd=xxxxx */
	if((INSWORD & 0x7FA00000)==0x13800000) {
		decode_fields32(ENC_EXTR_32_EXTRACT, dec);
		dec->d = UINT(dec->Rd);
		dec->n = UINT(dec->Rn);
		dec->m = UINT(dec->Rm);
		dec->datasize = (dec->sf==1) ? 0x40 : 0x20;
		if(dec->N!=dec->sf) {
			UNDEFINED;
		}
		if(dec->sf==0 && SLICE(dec->imms,5,5)==1) {
			UNDEFINED;
		}
		dec->lsb = UINT(dec->imms);
		/* regular aliases */
		if(dec->Rn==dec->Rm) return ROR_EXTR(ctx, dec);
		if(dec->sf==0 && dec->N==0 && !(dec->imms&0x20)) OK(ENC_EXTR_32_EXTRACT);
		if(dec->sf==1 && dec->N==1) OK(ENC_EXTR_64_EXTRACT);
	}
	return rc;
}

