/* psb.xml */
int PSB(context *ctx, Instruction *dec)
{
	int rc = DECODE_STATUS_UNMATCHED;
	/* class PSB_HC_hints */
	/* 1101010100|L=0|op0=00|op1=011|CRn=0010|CRm=0010|op2=001|Rt=11111 */
	if((INSWORD & 0xFFFFFFFF)==0xD503223F) {
		decode_fields32(ENC_PSB_HC_HINTS, dec);
		if(!((dec->CRm<<3)|dec->op2)) {
			dec->op = SystemHintOp_NOP;
		}
		else if(((dec->CRm<<3)|dec->op2)==1) {
			dec->op = SystemHintOp_YIELD;
		}
		else if(((dec->CRm<<3)|dec->op2)==2) {
			dec->op = SystemHintOp_WFE;
		}
		else if(((dec->CRm<<3)|dec->op2)==3) {
			dec->op = SystemHintOp_WFI;
		}
		else if(((dec->CRm<<3)|dec->op2)==4) {
			dec->op = SystemHintOp_SEV;
		}
		else if(((dec->CRm<<3)|dec->op2)==5) {
			dec->op = SystemHintOp_SEVL;
		}
		else if(((dec->CRm<<3)|dec->op2)==6) {
			if(!HaveDGHExt()) {
				ENDOFINSTRUCTION;
			}
			dec->op = SystemHintOp_DGH;
		}
		else if(((dec->CRm<<3)|dec->op2)==7) {
			SEE /* XPACLRI */;
		}
		else if((((dec->CRm<<3)|dec->op2)&0x78)==8) {
			if(!dec->op2) {
				SEE /* PACIA1716 */;
			}
			else if(dec->op2==2) {
				SEE /* PACIB1716 */;
			}
			else if(dec->op2==4) {
				SEE /* AUTIA1716 */;
			}
			else if(dec->op2==6) {
				SEE /* AUTIB1716 */;
			}
			else {
				ENDOFINSTRUCTION;
			}
		}
		else if(((dec->CRm<<3)|dec->op2)==0x10) {
			if(!HaveRASExt()) {
				ENDOFINSTRUCTION;
			}
			dec->op = SystemHintOp_ESB;
		}
		else if(((dec->CRm<<3)|dec->op2)==0x11) {
			if(!HaveStatisticalProfiling()) {
				ENDOFINSTRUCTION;
			}
			dec->op = SystemHintOp_PSB;
		}
		else if(((dec->CRm<<3)|dec->op2)==0x12) {
			if(!HaveSelfHostedTrace()) {
				ENDOFINSTRUCTION;
			}
			dec->op = SystemHintOp_TSB;
		}
		else if(((dec->CRm<<3)|dec->op2)==0x14) {
			dec->op = SystemHintOp_CSDB;
		}
		else if((((dec->CRm<<3)|dec->op2)&0x78)==0x18) {
			if(!dec->op2) {
				SEE /* PACIAZ */;
			}
			else if(dec->op2==1) {
				SEE /* PACIASP */;
			}
			else if(dec->op2==2) {
				SEE /* PACIBZ */;
			}
			else if(dec->op2==3) {
				SEE /* PACIBSP */;
			}
			else if(dec->op2==4) {
				SEE /* AUTIAZ */;
			}
			else if(dec->op2==5) {
				SEE /* AUTHASP */;
			}
			else if(dec->op2==6) {
				SEE /* AUTIBZ */;
			}
			else if(dec->op2==7) {
				SEE /* AUTIBSP */;
			}
		}
		else if((((dec->CRm<<3)|dec->op2)&0x79)==0x20) {
			dec->op = SystemHintOp_BTI;
			SetBTypeCompatible(BTypeCompatible_BTI(SLICE(dec->op2,2,1), ctx->pstate_btype));
		}
		else {
			ENDOFINSTRUCTION;
		}
		OK(ENC_PSB_HC_HINTS);
	}
	return rc;
}

