/* sbfm.xml */
int SBFM(context *ctx, Instruction *dec)
{
	int rc = DECODE_STATUS_UNMATCHED;
	/* class signed_fill */
	/* sf=x|opc=00|100110|N=x|immr=xxxxxx|imms=xxxxxx|Rn=xxxxx|Rd=xxxxx */
	if((INSWORD & 0x7F800000)==0x13000000) {
		decode_fields32(ENC_SBFM_32M_BITFIELD, dec);
		dec->d = UINT(dec->Rd);
		dec->n = UINT(dec->Rn);
		dec->datasize = (dec->sf==1) ? 0x40 : 0x20;
		if(!dec->opc) {
			dec->inzero = TRUE;
			dec->extend = TRUE;
		}
		else if(dec->opc==1) {
			dec->inzero = FALSE;
			dec->extend = FALSE;
		}
		else if(dec->opc==2) {
			dec->inzero = TRUE;
			dec->extend = FALSE;
		}
		else if(dec->opc==3) {
			UNDEFINED;
		}
		if(dec->sf==1 && dec->N!=1) {
			UNDEFINED;
		}
		if(dec->sf==0 && (dec->N!=0 || SLICE(dec->immr,5,5)!=0 || SLICE(dec->imms,5,5)!=0)) {
			UNDEFINED;
		}
		dec->R = UINT(dec->immr);
		dec->S = UINT(dec->imms);
		DecodeBitMasks_ReturnType dbmrt = DecodeBitMasks(dec->N,dec->imms,dec->immr);
		dec->wmask = dbmrt.wmask;
		dec->tmask = dbmrt.tmask;
		/* regular aliases */
		bool encoding32 = dec->sf==0 && dec->N==0;
		bool encoding64 = dec->sf==1 && dec->N==1;
		if((dec->imms==0x1f && EncodingLabeled32Bit()) || (dec->imms==0x3f && EncodingLabeled64Bit())) return ASR_SBFM(ctx, dec);
		if(UINT(dec->imms)<UINT(dec->immr)) return SBFIZ_SBFM(ctx, dec);
		if(BFXPreferred(dec->sf,SLICE(dec->opc,1,1),dec->imms,dec->immr)) return SBFX_SBFM(ctx, dec);
		if(dec->immr==0 && dec->imms==7) return SXTB_SBFM(ctx, dec);
		if(dec->immr==0 && dec->imms==15) return SXTH_SBFM(ctx, dec);
		if(dec->immr==0 && dec->imms==0x1f) return SXTW_SBFM(ctx, dec);
		if(dec->sf==0 && dec->N==0) OK(ENC_SBFM_32M_BITFIELD);
		if(dec->sf==1 && dec->N==1) OK(ENC_SBFM_64M_BITFIELD);
	}
	return rc;
}

